import React from 'react';
import { Nav, Icon } from 'rsuite';
import { Switch, Route } from 'react-router-dom'; 
import 'rsuite/dist/styles/rsuite-dark.css';
import TournamentList from '../TournamentList/TournamentList';


const styles = {
  marginBottom: 50
};

const CustomNav = ({ active, onSelect, ...props }) => {
  return (
    <Switch>
      <Nav {...props} activeKey={active} onSelect={onSelect} style={styles}>
        <Nav.Item eventKey="home" icon={<Icon icon="home" />}>
          Home
        </Nav.Item>
        <Route exact path="/" component={TournamentList}><Nav.Item eventKey="news">Tournaments</Nav.Item></Route>
        <Nav.Item eventKey="solutions">Fixtures</Nav.Item>
        <Nav.Item eventKey="products">Results</Nav.Item>
        <Nav.Item eventKey="about">About</Nav.Item>
      </Nav>
    </Switch>
  );
};

class Navigation extends React.Component {
  constructor() {
    super();
    this.state = {
      active: 'home'
    };
    this.handleSelect = this.handleSelect.bind(this);
  }
  handleSelect(activeKey) {
    this.setState({ active: activeKey });
  }
  render() {
    const { active } = this.state;
    return (
      <div>
        <CustomNav appearance="subtle" active={active} onSelect={this.handleSelect} />
      </div>
    );
  }
}

export default Navigation
