import React from 'react';
import { Panel, PanelGroup, Button } from 'rsuite';
import 'rsuite/dist/styles/rsuite-dark.css';

const tournamentsArray = [];


function getTournaments() {
    fetch('https://api.pandascore.co/csgo/tournaments?page[size]=15&token=JA7Ng39D7acv1y9kU26iBrtVx_P72XlN8UZm67ICanRnauPvIDM')
    .then(resp => resp.json())
    .then(data => tournamentsArray.push(data));
}



const Tournaments = () => {
    const TournamentList = (tournamentsArray) => {
        const tournamentsList = tournamentsArray.map(tournament => {
            return (<Panel header={tournament} />)  
        })
    }
        return (
        <div>
            <Button onClick={getTournaments}>GET TOURNAMENTS</Button>
            {tournamentsList}
        </div>
        );
      }

export default Tournaments

