import React from 'react';
import { Panel, PanelGroup, Button } from 'rsuite';
import 'rsuite/dist/styles/rsuite-dark.css';

class TournamentList extends React.Component {
    constructor(props){
        super(props); 
        this.state = {
            tournamentListArray: [],
            tournamentsArray: [],
            tournamentsList: [],
        }
    }

    getTournaments = () => {
        fetch('https://api.pandascore.co/csgo/tournaments?page[size]=15&token=JA7Ng39D7acv1y9kU26iBrtVx_P72XlN8UZm67ICanRnauPvIDM')
        .then(resp => resp.json())
        .then(data => this.state.tournamentsArray.push(data))
        .then(console.log(this.state.tournamentsArray))
    }


    handleApiCall = () => {
        console.log(this.state.tournamentsArray[0][0].id)
    }

    formatTournamentName = () =>  {
        const tournamentsList = this.state.tournamentsArray[0].map(obj => { 
            const tourObj = {};
            tourObj[obj.league.name] = obj.slug;
            return tourObj
        })
        console.log(tournamentsList);
        this.state.tournamentListArray.push(tournamentsList)
        return tournamentsList;
    }

    showTournaments = (tournamentListArray) => {
        const tournaments = this.state.tournamentListArray.map( tournament => {
        return <Panel >{tournament}</Panel>
        })

    }

    render() {
        return (
            <div>
                <Button onClick={this.getTournaments}>GET TOURNAMENTS</Button>
                <Button onClick={this.handleApiCall}>CHECK API</Button>
                <Button onClick={this.formatTournamentName}>FORMAT TOURNAMENTS</Button>
                <Panel onClick={this.showTournaments} ></Panel>
            </div>
            )  
        }
    }

export default TournamentList 

