import React from 'react';
import { Panel, PanelGroup, Button } from 'rsuite';
import 'rsuite/dist/styles/rsuite-dark.css';

class TournamentList extends React.Component {
    constructor(props){
        super(props); 
        this.state = {
            isLoaded: false,
            error: null,
            tournamentsArray: [],
            tournamentsList: [],
        }
    }

    componentDidMount() {
        fetch('https://api.pandascore.co/csgo/tournaments?page[size]=15&token=JA7Ng39D7acv1y9kU26iBrtVx_P72XlN8UZm67ICanRnauPvIDM')
        .then(res => res.json())
        .then (
            (data) => {
                console.log(data[2].slug)
                const dataArray = data[0].map(obj => { 
                    const tourObj = {};
                    tourObj[obj.league.name] = obj.slug;
                    return tourObj
                })
                // console.log(dataArray);
                // this.state.tournamentListArray.push(tournamentsList)
                // console.log(this.tournamentListArray);
                return dataArray;
                // Push data to Array
                this.setState({
                    isLoaded: true,
                    tournamentsArray: data
                });

                // this.formatTournamentName();
            },
            //error handler
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                })
            }
        )
    }

    // getTournaments = () => {
    //     fetch('https://api.pandascore.co/csgo/tournaments?page[size]=15&token=JA7Ng39D7acv1y9kU26iBrtVx_P72XlN8UZm67ICanRnauPvIDM')
    //     .then(resp => resp.json())
    //     .then(data => this.state.tournamentsArray.push(data))
    //     .then(console.log(this.state.tournamentsArray))
    // }


    handleApiCall = () => {
        console.log(this.state.tournamentsArray[0][0].id)
    }

    formatTournamentName = (tournamentsArray) =>  {
        const tournamentsList = tournamentsArray[0].map(obj => { 
            const tourObj = {};
            tourObj[obj.league.name] = obj.slug;
            return tourObj
        })
        console.log(tournamentsList);
        this.state.tournamentListArray.push(tournamentsList)
        console.log(this.tournamentListArray);
        return tournamentsList;
    }


    render() {
        const { error, isLoaded, tournamentListArray  } = this.state

        if (error) {
            return (
                <div> 
                    Error: {error.message}
                </div>
            )
        } else if (!isLoaded) {
            return(
                <div>
                    Loading...
                </div>
            )
        } else {
            return (
                <div>
                    <Button onClick={this.formatTournamentName}>Format tournament name </Button>
                    <PanelGroup accordion bordered>
                        <Panel></Panel>
                        <Panel header="Tournament placeholder #3">
                            <p>Tournament placeholder</p>
                        </Panel>
                    </PanelGroup> 
                </div>

            )
        }
    }    

}

export default TournamentList 

