import React from 'react';
import { Nav, Icon } from 'rsuite';
import { Link, Route, Switch } from 'react-router-dom'; 
import 'rsuite/dist/styles/rsuite-dark.css';
import TournamentList from '../TournamentList/TournamentList';


const CustomNav = ({ active, onSelect, ...props }) => {
  return (
        <Nav {...props} activeKey={active} onSelect={onSelect} >
            <Nav.Item href="/" eventKey="home" icon={<Icon icon="home" />}>Home</Nav.Item>
            <Nav.Item href="/tournaments" eventKey="tournaments">Tournaments</Nav.Item>
            <Nav.Item href="/fixtures" eventKey="fixtures">Fixtures</Nav.Item> 
            <Nav.Item href="/results" eventKey="results">Results</Nav.Item>
            <Nav.Item href="/about" eventKey="about">About</Nav.Item>
        </Nav>
        );
};

const MyLink = React.forwardRef((props, ref) => {
  const { href, as, ...rest } = props;
  return (
    <Link href={href} as={as}>
      <a ref={ref} {...rest} />
    </Link>
  );
});

const NavLink = props => <Nav.Item componentClass={MyLink} {...props} />;
const Instance = (
  <Nav>
    <NavLink href="/">Home</NavLink>
    <NavLink href="/guide/introduction">Guide</NavLink>
    <NavLink href="/components/overview">Components</NavLink>
    <NavLink href="/tools/palette">Tools</NavLink>
  </Nav>
);

class Navigation extends React.Component {
  constructor() {
    super();
    this.state = {
      active: 'home'
    };
    this.handleSelect = this.handleSelect.bind(this);
  }
  handleSelect(activeKey) {
    this.setState({ active: activeKey });
  }
  render() {
    const { active } = this.state;
    return (
      <div>
        <CustomNav appearance="subtle" active={active} onSelect={this.handleSelect} />
        <Instance></Instance>
      </div>
    );
  }
}

export default Navigation
