import { ReactComponent } from '*.svg';
import React from 'react';
import { Panel, PanelGroup, Button } from 'rsuite';
import 'rsuite/dist/styles/rsuite-dark.css';


let tournamentsArray = [];


function getTournaments() {
    fetch('https://api.pandascore.co/csgo/tournaments?page[size]=15&token=JA7Ng39D7acv1y9kU26iBrtVx_P72XlN8UZm67ICanRnauPvIDM')
    .then(resp => resp.json())
    .then(data => tournamentsArray.push(data))
    .then(console.log(tournamentsArray))
}


function handleApiCall() {
    console.log(tournamentsArray[0][0].id)
}

function formatTournamentName() {
    const tournamentsList = tournamentsArray[0].map(obj => { 
        const tourObj = {};
        tourObj[obj.league.name] = obj.slug;
        return tourObj
    })
    console.log(tournamentsList);
    tournamentListArray.push(tournamentsList)
    return tournamentsList;
}



class TournamentList extends React.Component {
    constructor(props){
        super(props); 
        this.state = {
            tournamentListArray: []
        }
    }

render() {
    return (
        <div>
            <Button onClick={getTournaments}>GET TOURNAMENTS</Button>
            <Button onClick={handleApiCall}>CHECK API</Button>
            <Button onClick={formatTournamentName}>FORMAT TOURNAMENTS</Button>
        </div>
        )  
    }
}

export default TournamentList 

