import React from 'react';
import { Nav, Icon } from 'rsuite';
import { Link, Route, Switch } from 'react-router-dom'; 
import 'rsuite/dist/styles/rsuite-dark.css';
import TournamentList from '../TournamentList/TournamentList';


const CustomNav = ({ active, onSelect, ...props }) => {
  return (
        <Nav {...props} activeKey={active} onSelect={onSelect} className="rs-nav-subtle rs-nav-horizontal rs-nav-item-active" >
            <Link to="/"> <Nav.Item eventKey="home" icon={<Icon icon="home" />}>Home</Nav.Item></Link>
            <Link to="/tournaments" ><Nav.Item eventKey="tournaments">Tournaments</Nav.Item></Link>
            <Link to="/fixtures" ><Nav.Item eventKey="fixtures">Fixtures</Nav.Item> </Link>
            <Link to="/results" ><Nav.Item eventKey="results">Results</Nav.Item> </Link>
            <Link to="/about"><Nav.Item eventKey="about">About</Nav.Item></Link>
        </Nav>
        );
};

class Navigation extends React.Component {
  constructor() {
    super();
    this.state = {
      active: 'home'
    };
    this.handleSelect = this.handleSelect.bind(this);
  }
  handleSelect(activeKey) {
    this.setState({ active: activeKey });
  }
  render() {
    const { active } = this.state;
    return (
      <div>
        <CustomNav appearance="subtle" active={active} onSelect={this.handleSelect} />
      </div>
    );
  }
}

export default Navigation
