    //     .then(resp => resp.json())
    //     .then(data => this.state.tournamentsArray.push(data))
    //     .then(console.log(this.state.tournamentsArray))
    // }


    handleApiCall = () => {
        console.log(this.state.tournamentsArray[0][0].id)
    }

    formatTournamentName = () =>  {
        const tournamentsList = this.state.tournamentsArray[0].map(obj => { 
            const tourObj = {};
            tourObj[obj.league.name] = obj.slug;
            return tourObj
        })
        console.log(tournamentsList);
        this.state.tournamentListArray.push(tournamentsList)
        return tournamentsList;
    }

    showTournaments = () => {
        this.forceUpdate();
    }

    render() {
        return (
            <div>
                <Button onClick={this.getTournaments}>GET TOURNAMENTS</Button>
                <Button onClick={this.handleApiCall}>CHECK API</Button>
                <Button onClick={this.formatTournamentName}>FORMAT TOURNAMENTS</Button>
                <Button onClick={this.showTournaments} >Show Tournaments</Button>
                <PanelGroup accordion bordered>
                    <Panel header={this.state.tournamentListArray}>
                        <p>GRGAAAA</p>
                    </Panel>
                    <Panel header="Tournament placeholder #3">
                        <p>Tournament placeholder</p>
                    </Panel>
                </PanelGroup> 
            </div>
            )  
        }
    }

export default TournamentList 

