import React from 'react';
import { Panel, PanelGroup, Button } from 'rsuite';
import 'rsuite/dist/styles/rsuite-dark.css';

class TournamentList extends React.Component {
    constructor(props){
        super(props); 
        this.state = {
            isLoaded: false,
            error: null,
            tournamentsArray: [],
            tournamentsList: [],
        }
    }

    componentDidMount() {
        fetch('https://api.pandascore.co/csgo/tournaments?page[size]=15&token=JA7Ng39D7acv1y9kU26iBrtVx_P72XlN8UZm67ICanRnauPvIDM')
        .then(res => res.json())
        .then (
            (data) => {
                console.log(data[2].slug)
                const dataArray = data.map(obj => { 
                    const tourObj = {};
                    tourObj[obj.league.name] = obj.slug;
                    return tourObj
                })
                // console.log(dataArray);
                // this.state.tournamentListArray.push(tournamentsList)
                // console.log(this.tournamentListArray);
                // Push data to Array
                this.setState({
                    isLoaded: true,
                    tournamentsArray: dataArray
                });

                // this.formatTournamentName();
            },
            //error handler
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                })
            }
        )
    }

    // getTournaments = () => {
