    fetch('https://api.pandascore.co/csgo/tournaments?page[size]=15&token=JA7Ng39D7acv1y9kU26iBrtVx_P72XlN8UZm67ICanRnauPvIDM')
    .then(resp => resp.json())
    .then(data => tournamentsArray.push(data))
    .then(console.log(tournamentsArray))
}


function handleApiCall() {
    console.log(tournamentsArray[0][0].id)
}

function formatTournamentName() {
    const tournamentsList = tournamentsArray[0].map(obj => { 
        const tourObj = {};
        tourObj[obj.league.name] = obj.slug;
        return tourObj
